package variable;

public class Mapthos1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=200;
		int b=10;
		int num1=a+b;
		int num2=a-b;
		int num3=a*b;
		int num4=a/b;
		int num5=a%b;
		System.out.println("PRGRAM STATING");
		System.out.println("************************************");
		System.out.println("VALUE OF a is: "+a);
		System.out.println("VALUE OF b is:"+b);
		System.out.println("&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&");
		System.out.println("num1 ADDITION is: "+num1);
		System.out.println("num2 SUBSTRACTION is:"+num2);
		System.out.println("num3 MULTIPLICATION is:"+num3);
		System.out.println("num4 DIVISION is:"+num4);
		System.out.println("num5 MODULE is: "+num4);
		System.out.println("********************************************");
		System.out.println("PROGRAM ENDS");

	}

}
